namespace SimpleEmbedding.Pages
{
    public partial class Home
    {
        private string url { get; set; }
        private string subform { get; set; }
        protected override async Task OnInitializedAsync()
        {
            
        }

        private async Task RunCommand()
        {
            await FetchBusinessRuleSchema();
        }

        private async Task FetchBusinessRuleSchema()
        {
            var client = new HttpClient();
            var request = new HttpRequestMessage(HttpMethod.Get, $"http://localhost:6868//PhoebusAPI/CRM/pbs_BO_Rules_UIFieldRules?ClassName={url.Split('/').FirstOrDefault()}&Subform={url.Split('/').LastOrDefault()}");
            request.Headers.Add("token", "");
            var response = await client.SendAsync(request);
            if(response.IsSuccessStatusCode)
            {
                var json=await response.Content.ReadAsStringAsync();
                if(json!=null)
                {
                    if(!File.Exists(@$"D:\\Project Internal\\Sample-Form.io-.Net\\SimpleEmbedding\\BusinessRuleSchema\\businessSchema#{url.Replace('/','#')}.json"))
                    {
                        System.IO.File.WriteAllText(@$"D:\Project Internal\Sample-Form.io-.Net\SimpleEmbedding\BusinessRuleSchema\businessSchema#{url.Replace('/', '#')}.json", string.Empty);
                        using (StreamWriter outputFile = new StreamWriter(@$"D:\Project Internal\Sample-Form.io-.Net\SimpleEmbedding\BusinessRuleSchema\businessSchema#{url.Replace('/', '#')}.json"))
                        {
                            outputFile.WriteLine(json);
                            outputFile.Close();
                        }
                    }
                }
            }
            nav.NavigateTo($"phoebusweb/{url.Replace('/','_')}");
        }
    }
}
